mapper.select:
SELECT REVIEWID, USERID, REVIEWTITLE, REVIEWCONTENT, UPLOADIMG, REGDATE, TRAINERID, TRAINER FROM (SELECT SEQ, REVIEWID, USERID, REVIEWTITLE, REVIEWCONTENT, UPLOADIMG, REGDATE, TRAINERID, TRAINER FROM (SELECT ROWNUM AS SEQ, REVIEWID, USERID, REVIEWTITLE, REVIEWCONTENT, UPLOADIMG, REGDATE, TRAINERID, TRAINER FROM (SELECT * FROM PSTM_REVIEWINFO
REVIEWTITLE LIKE '%' || #{reviewTitle}|| '%'
TRAINER LIKE '%' || #{trainerName} || '%'
)) WHERE SEQ > #{offset}) WHERE ROWNUM <= #{count}
